// state 생성 : 1) state를 바꾸고 싶을 때
const initialState = {
  name: 'nasby',
  age: 11,
  password: 'babo',
};

/*
// 일회성 액션의 예제
const changeNickname = {
  type: 'CHANGE_NICKNAME',
  data: 'sangari',
}
*/

// action : 2) 액션을 하나 만들어서 store에 store.dispatch
// 자주 바뀌는 data일 경우 동적 액션을 담은 함수를 생성해 주는 것이 좋음 (action creator)
const changeNickname = (data) => {
  return {
    type: 'CHANGE_NICKNAME',
    data,
  }
};

changeNickname('sangari');
store.dispatch(changeNickname('jm'));



// reducer : 3) 이전상태와 액션을 통해서 다음 상태를 만들어내는 함수 / (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'CHANGE_NICKNAME':
      return {
        ...state, // state의 다른 내용은 그대로 두되
        name: action.data, // name만 action.data로 바꾸겠다
      }
  }
};

export default rootReducer;
