import { HYDRATE } from 'next-redux-wrapper';

// state 생성 : 1) state를 바꾸고 싶을 때
const initialState = {
  user: {
    isLoggedIn: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  }
};


// action : 2) 액션을 하나 만들어서 store에 store.dispatch
export const loginAction = (data) => {
  return {
    type: 'LOG_IN',
    data,
  }
}

export const logoutAction = (data) => {
  return {
    type: 'LOG_OUT',
  }
}


// reducer : 3) 이전상태와 액션을 통해서 다음 상태를 만들어내는 함수 / (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case HYDRATE :
      console.log('HYDRATE', action);
      return { ...state, ...action.payload };
    case 'LOG_IN':
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: true,
          user: action.data,
        }        
      };
    case 'LOG_OUT':
      return {
        ...state,
        user: {
          ...state.user,
          isLoggedIn: false,
          user: null,
        }
      };
    default:
      return state;
  }
};

export default rootReducer;
