// state 생성 : 1) state를 바꾸고 싶을 때
const initialState = {
  name: 'nasby',
  age: 11,
  password: 'babo',
};

// action : 2) 액션을 하나 만들어서 store에 store.dispatch
const changeNickname = {
  type: 'CHANGE_NICKNAME',
  data: 'sangari',
}

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'CHANGE_NICKNAME':
      return {
        ...state,
        name: action.data,
      }
  }
};

export default rootReducer;
